@mixin mobile {
  @media screen and (max-width: #{$tablet-min - 1}) {
    @content;
  }
}

@mixin tablet {
  @media screen and (min-width: $tablet-min) {
    @content;
  }
}

@mixin tablet-only {
  @media screen and (max-width: #{$tablet-min}) and (max-width: #{$desktop-min - 1}) {
    @content;
  }
}

@mixin desktop {
  @media screen and (min-width: #{$desktop-min}) {
    @content;
  }
}

@mixin bigger-desktop {
  @media screen and (min-width: $bigger-desktop-min) {
    @content;
  }
}

@mixin clearfix {
  ::after {
    content: "";
    clear: both;
    display: table;
  }
}

@mixin transform($property) {
  -webkit-transform: $property;
  -ms-transform: $property;
  transform: $property;
}

