.feedback {
  $this: &;

  width: 100%;

  @include tablet {
    margin: 40px 40px;
  }

  @include desktop {
    order: 2;
    width: 825px;
    margin: 94px auto 0 30px;
  }

  &__controls {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 0 vwm(24);
    height: vwm(56);
    background-color: #373f68;
    color: #f2f4fe;
    margin-bottom: 32px;

    @include tablet {
      border-radius: 10px;
      height: 72px;
      margin-bottom: 24px;
    }

    @include desktop {
      padding-top: unset;
    }

    #{$this}__counter {
      display: none;

      @include tablet {
        display: flex;
        align-items: center;
      }

      & .bulb {
        height: 24px;
        width: 23px;
        background-image: url(/src/assets/images/suggestions/bulb.png);
        background-size: cover;
        margin-right: 16px;
      }

      & .count {
        margin-right: 5px;
        display: flex;
        align-items: center;
        font-weight: bold;
        font-size: 18px;
        line-height: 26px;
        letter-spacing: -0.25px;
      }

      & h3 {
        height: 100%;
        font-weight: bold;
        font-size: 18px;
        line-height: 26px;
        letter-spacing: -0.25px;
      }
    }

    #{$this}__sort {
      font-size: 14px;
      line-height: 20px;
      display: flex;
      cursor: pointer;

      & .arrow {
        width: 8px;
        height: 4px;
        display: inline-block;
        align-self: center;
        background-image: url(/src/assets/images/suggestions/white-arrow.png);
        background-size: cover;
      }
    }

    & button {
      background: #ad1fea;
      border-radius: 10px;
      width: 158px;
      height: 44px;
      font-weight: bold;
      font-size: 14px;
      line-height: 20px;
    }
  }

  & .feedback-items-wraper {
    margin: 24px;

    @include tablet {
      margin: 0;
    }

    & .feedback-item {
      display: flex;
      flex-wrap: wrap;
      font-weight: bold;
      font-size: 13px;
      line-height: 19px;
      letter-spacing: -0.180556px;
      color: #3a4374;
      padding: 24px;
      border-radius: 10px;
      background: #ffffff;
      margin-bottom: 16px;

      @include tablet {
        padding: 28px 32px;
        min-height: 151px;
        overflow: auto;
        margin-bottom: 20px;
      }

      &__left {
        order: 2;
        flex: 1;
        display: flex;

        @include tablet {
          order: 1;
          flex: 0 1 auto;
          flex-direction: column;
          margin-right: 40px;
          padding: 14px 11px 0;
        }

        & .arrow {
          width: 8px;
          height: 4px;
          background-image: url(/src/assets/images/shared/icon-arrow-up.svg);
          background-size: cover;
          display: inline-block;
          align-self: center;
          margin-right: 10px;

          @include tablet {
            margin: 0 auto 8px;
          }
        }
      }

      &__left,
      &__right {
        display: flex;
      }

      &__center {
        order: 1;
        flex: 100%;

        @include tablet {
          order: 2;
          flex: 0 1 auto;
        }

        & h4 {
          font-weight: bold;

          @include tablet {
            font-size: 18px;
            line-height: 26px;
            letter-spacing: -0.25px;
          }
        }

        & p {
          color: #647196;
          margin-top: 8px;

          @include tablet {
            font-size: 16px;
            line-height: 23px;
          }
        }

        & .tag {
          margin: 8px 0 16px;

          & span {
            color: #4661e6;
            font-weight: 600;
            background: #f2f4ff;
            border-radius: 10px;
            display: inline-flex;
            align-items: center;
            justify-content: center;
            width: auto;
            height: 30px;
            padding: 0 16px;

            @include tablet {
              font-size: 13px;
              line-height: 19px;
            }
          }
        }
      }

      &__right {
        order: 3;
        flex: 1;
        justify-content: flex-end;

        @include tablet {
          padding-top: 40px;
        }

        @include desktop {
          order: 3;
        }

        & .comment {
          width: 18px;
          height: 16px;
          background-image: url(/src/assets/images/shared/icon-comments.svg);
          background-size: cover;
          margin-right: 10px;
          font-size: 13px;
        }
      }
    }
  }
}
