.sidebar {
  $this: &;

  position: fixed;
  width: 100%;
  display: flex;
  z-index: 2;

  @include tablet {
    position: relative;
    overflow: visible;
    height: vwt(178);
    margin: 56px 40px 40px;
  }

  @include desktop {
    margin: 94px 0 0 auto;
    width: 255px;
    flex-direction: column;
  }

  &__titles {
    display: flex;
    justify-content: space-between;
    align-items: center;
    height: 20vw;
    background: radial-gradient(
      128.88% 128.88% at 103.9% -10.39%,
      #e84d70 0%,
      #a337f6 53.09%,
      #28a7ed 100%
    );
    color: #ffffff;
    padding: 0 24px;
    width: 100%;

    @include tablet {
      align-items: flex-end;
      padding: 24px;
      width: vwt(223);
      height: 100%;
      border-radius: 10px;
    }

    @include desktop {
      width: unset;
      min-height: 137px;
    }

    h1 {
      font-size: 15px;
      line-height: 22px;
      letter-spacing: -0.1875px;
      font-weight: bold;

      @include tablet {
        font-size: 20px;
        line-height: 29px;
        letter-spacing: -0.25px;
      }
    }

    h3 {
      font-size: 13px;
      line-height: 19px;
      mix-blend-mode: normal;

      @include tablet {
        font-size: 15px;
        line-height: 22px;
        color: #ffffff;
        opacity: 0.75;
        font-weight: 500;
      }
    }

    & .toggleButton {
      width: vwm(20);
      height: vwm(17);
      display: flex;
      flex-wrap: wrap;
      align-items: space-between;

      @include tablet {
        display: none;
      }

      & > div {
        width: 100%;
        height: vwm(3);
        background: white;
      }
    }
  }

  &__menu {
    position: absolute;
    height: calc(100vh - 20vw);
    width: vwm(271);
    top: 20vw;
    right: 0;
    display: flex;
    flex-direction: column;
    padding: 24px;
    background: transparent;
    transform: translateX(100%);
    transition: 0.3s;
    z-index: 10;

    &--visible {
      transition: 0.3s;
      transform: translateX(0%);
    }

    @include tablet {
      position: relative;
      flex-direction: row;
      top: 0;
      width: calc(100% - 29.03646vw);
      height: 100%;
      transform: translateX(0%);
      padding: 0;
    }

    @include desktop {
      width: 100%;
      flex-direction: column;
    }

    & > div {
      width: 100%;
      height: vwm(178);
      background: #ffffff;
      border-radius: 10px;

      @include tablet {
        height: unset;
        margin-left: 10px;
      }

      @include desktop {
        margin-left: 0px;
        padding: 24px;
      }
    }

    #{$this}__categories {
      display: flex;
      flex-wrap: wrap;
      align-content: flex-start;
      padding: 1vw;

      @include tablet {
        padding: 24px;
      }

      & > button {
        font-size: 13px;
        line-height: 19px;
        color: #4661e6;
        padding: 5.5px 16px;
        margin: 0 8px 16px 0;
        border-radius: 10px;
        font-weight: 600;
        cursor: pointer;

        &:nth-child(1),
        &:nth-child(2),
        &:nth-child(3) {
          width: 48px;
        }

        &:hover {
          background-color: #cfd7ff;
        }

        &:focus {
          background-color: #4661e6;
          color: #ffffff;
        }
      }

      @include desktop {
        margin-top: 24px;
      }
    }

    #{$this}__status-wrapper {
      margin-top: 24px;
      display: flex;
      flex-wrap: wrap;
      align-content: flex-start;
      align-items: center;
      justify-content: space-between;
      padding: 24px;

      @include tablet {
        margin-top: 0;
      }

      @include desktop {
        margin-top: 24px;
      }

      #{$this}__status-display {
        display: flex;
        flex-wrap: wrap;

        & span {
          color: #3a4374;
          font-size: 18px;
          font-weight: 700;
        }

        & a {
          font-weight: 600;
          font-size: 13px;
          line-height: 19px;
          text-decoration-line: underline;
          color: #4661e6;
        }

        #{$this}__status {
          font-size: 16px;
          line-height: 23px;
          color: #647196;
          margin-bottom: 8px;
          flex: 100%;
          display: flex;
          align-items: center;

          & .circle {
            border-radius: 50%;
            height: 8px;
            width: 8px;
            display: inline-block;
            margin-right: 16px;
          }

          & .count {
            line-height: 23px;
            text-align: right;
            font-weight: bold;
            font-size: 16px;
            line-height: 23px;
            margin-left: auto;
            color: #647196;
          }

          &--planned {
            margin-top: 24px;

            .circle {
              background-color: #f49f85;
            }
          }

          &--in-progress {
            .circle {
              background-color: purple;
            }
          }

          &--live {
            .circle {
              background-color: rgb(104, 171, 216);
            }
          }
        }
      }
    }
  }

  @include desktop {
  }
}

sources.src/assets/styles/base/mixins.scss:
@mixin mobile {
  @media screen and (max-width: #{$tablet-min - 1}) {
    @content;
  }
}

@mixin tablet {
  @media screen and (min-width: $tablet-min) {
    @content;
  }
}

@mixin tablet-only {
  @media screen and (max-width: #{$tablet-min}) and (max-width: #{$desktop-min - 1}) {
    @content;
  }
}

@mixin desktop {
  @media screen and (min-width: #{$desktop-min}) {
    @content;
  }
}

@mixin bigger-desktop {
  @media screen and (min-width: $bigger-desktop-min) {
    @content;
  }
}

@mixin clearfix {
  ::after {
    content: "";
    clear: both;
    display: table;
  }
}

@mixin transform($property) {
  -webkit-transform: $property;
  -ms-transform: $property;
  transform: $property;
}

sources.src/assets/styles/modules/2-feedback.scs:
.feedback {
  $this: &;

  width: 100%;
  position: relative;
  margin: 20vw 0 0 0;
  z-index: 1;

  @include tablet {
    margin: 40px 40px;
  }

  &--root {
    @include desktop {
      order: 2;
      width: 825px;
      margin: 94px auto 0 30px;
    }

    @include tablet {
      order: 2;
    }
    & {
      .feedback-item__left {
        order: 2;
      }

      @include tablet {
        order: 1;
      }

      & {
        .feedback-item__center {
          order: 1;

          @include tablet {
            order: 2;
          }
        }
      }
    }
  }

  &--details {
    width: auto;
  }

  &--roadmap {
    @include tablet {
      margin: 30px 30px 0 0;
      width: 100%;
    }

    & {
      .feedback-item__left {
        order: 2;
      }
    }

    & {
      .feedback-item__center {
        order: 1;
      }
    }
  }

  &__controls {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 0 vwm(24);
    height: vwm(56);
    background-color: #373f68;
    color: #f2f4fe;
    margin-bottom: 32px;

    @include tablet {
      border-radius: 10px;
      height: 72px;
      margin-bottom: 24px;
    }

    @include desktop {
      padding-top: unset;
    }

    #{$this}__counter {
      display: none;

      @include tablet {
        display: flex;
        align-items: center;
      }

      & .bulb {
        height: 24px;
        width: 23px;
        background-image: url(/src/assets/images/suggestions/bulb.png);
        background-size: cover;
        margin-right: 16px;
      }

      & .count {
        margin-right: 5px;
        display: flex;
        align-items: center;
        font-weight: bold;
        font-size: 18px;
        line-height: 26px;
        letter-spacing: -0.25px;
      }

      & h3 {
        height: 100%;
        font-weight: bold;
        font-size: 18px;
        line-height: 26px;
        letter-spacing: -0.25px;
      }
    }

    #{$this}__sort {
      position: relative;
      font-size: 14px;
      line-height: 20px;
      display: flex;
      cursor: pointer;
      width: 200px;
      cursor: pointer;

      & .arrow {
        width: 8px;
        height: 4px;
        display: inline-block;
        align-self: center;
        background-image: url(/src/assets/images/suggestions/white-arrow.png);
        background-size: cover;
        margin-left: auto;
        transition: 0.5s transform;

        &--rotated {
          transition: 0.5s transform;
          transform: rotateX(180deg);
        }
      }

      & .text {
        min-height: 30px;
        display: inline-flex;
        align-items: center;
        cursor: pointer;
        padding-right: 15px;

        @include tablet {
          min-width: 200px;
        }
      }

      & .current {
        padding-left: 5px;
      }

      & .dropdown {
        padding: 66px 0 0 0;
        position: absolute;
        top: 26px;
        left: 0;
        width: 100%;
        height: 200px;
        display: none;

        @include desktop {
          padding: 46px 0 0 0;
          top: 26px;
        }

        &--visible {
          display: block;
        }

        & .dropdown-item {
          height: 50px;
          background-color: rgb(247, 242, 242);
          display: flex;
          padding-left: 10px;
          align-items: center;
          color: black;
          border-bottom: 0.5px solid rgb(207, 207, 207);
          justify-content: space-between;

          &:hover {
            color: purple;
            cursor: pointer;
          }

          &:nth-child(1) {
            border-top: 0.5px solid rgb(207, 207, 207);
          }

          & .checked {
            height: 15px;
            width: 15px;
            background: url("/src/assets/images/shared/icon-check.svg");
            background-repeat: no-repeat;
            background-size: contain;
            display: none;
            margin-right: 10px;
          }
        }
      }
    }

    & a.add {
      background-color: purple;
      display: inline-block;
      padding: 10px 16px;
      color: #f2f4fe;
      border-radius: 10px;
      cursor: pointer;
      min-width: 140px;
      font-size: 13px;

      @include desktop {
        font-size: 14px;
        line-height: 20px;
        min-width: unset;
      }
    }
  }

  & .feedback-items-wrapper {
    margin: 24px;

    @include tablet {
      margin: 0;
    }

    & .feedback-item {
      display: flex;
      flex-wrap: wrap;
      font-weight: bold;
      font-size: 13px;
      line-height: 19px;
      letter-spacing: -0.180556px;
      color: #3a4374;
      padding: 24px;
      border-radius: 10px;
      background: #ffffff;
      margin-bottom: 16px;
      cursor: pointer;

      @include tablet {
        padding: 28px 32px;
        min-height: 151px;
        overflow: auto;
        margin-bottom: 20px;
        height: 360px;
      }

      &__left {
        flex: 1;
        display: flex;

        @include tablet {
          flex: 0 1 auto;
          flex-direction: column;
          margin-right: 40px;
          padding: 14px 11px 0;
        }

        & .upvotes {
          cursor: pointer;
          border-radius: 5px;
          min-width: 40px;
          padding: 5px;
          display: flex;
          justify-content: center;
          align-items: center;
          flex-wrap: wrap;

          & > * {
            flex: 100%;
            text-align: center;
          }

          &:hover {
            background-color: rgb(173, 173, 173);
          }

          &--highlighted {
            background-color: rgb(58, 58, 196);
            color: white;
          }

          & .arrow {
            width: 8px;
            height: 4px;
            background-image: url(/src/assets/images/shared/icon-arrow-up.svg);
            background-size: cover;
            display: inline-block;
            align-self: center;
            margin: 0 10px 0 0;

            @include tablet {
              margin: 0 auto 8px;
            }
          }
        }
      }

      &__left,
      &__right {
        display: flex;
      }

      &__center {
        flex: 100%;

        @include tablet {
          flex: 0 1 auto;
        }

        & h4 {
          font-weight: bold;

          @include tablet {
            font-size: 18px;
            line-height: 26px;
            letter-spacing: -0.25px;
          }
        }

        & p {
          color: #647196;
          margin-top: 8px;

          @include tablet {
            font-size: 16px;
            line-height: 23px;
          }
        }

        & .tag {
          margin: 8px 0 16px;

          & span {
            color: #4661e6;
            font-weight: 600;
            background: #f2f4ff;
            border-radius: 10px;
            display: inline-flex;
            align-items: center;
            justify-content: center;
            width: auto;
            height: 30px;
            padding: 0 16px;

            @include tablet {
              font-size: 13px;
              line-height: 19px;
            }
          }
        }
      }

      &__right {
        order: 3;
        flex: 1;
        justify-content: flex-end;

        @include tablet {
          padding-top: 40px;
        }

        @include desktop {
          order: 3;
        }

        & .comment {
          width: 18px;
          height: 16px;
          background-image: url(/src/assets/images/shared/icon-comments.svg);
          background-size: cover;
          margin-right: 10px;
          font-size: 13px;
        }
      }
    }
  }
}

