    align-items: center;
    padding: 0 vwm(24);
    height: vwm(56);
    background-color: #373f68;
    color: #f2f4fe;
    margin-bottom: 32px;
    border-radius: 10px;

    @include tablet {
      height: 72px;
      margin-bottom: 24px;
    }

