      padding-top: unset;
    }

    & .arrow {
      width: 8px;
      height: 4px;
      display: inline-block;
      align-self: center;
      background-image: url(/src/assets/images/suggestions/white-arrow.png);
      background-size: cover;
      transform: rotate(90deg);
      margin-bottom: 3px;
